import React, { useState, Component } from "react"
import './Share.scss';
import share from "../../assets/share.svg";



export default class Share extends Component {

  copyLink = () => {

     /* get page url */

    const url= window.location.href;
    const copylink= url;
    this.url= document.execCommand("copy");

  }

  render() {
    return (
   <>
    <div className="Share">
      <button className="Share-toggle">
        <img src={share} alt="Share Icon" className="Share-icon"/>

        Share
      </button>
      <div className="Share-Body">
      <h4>Share covexit and help others during this crisis.</h4>
      </div>

          <button onClick="copyLink()">
            Copy Link
          </button>
          <button onClick={() => this.copyLink()} alert="success">
            Skip
          </button>

      </div>
 </>

    )
  }
}
