import React, { Component, useState } from "react"
import './Share.scss';
import share from "../../assets/share.svg";

function Share(){
   const [shareOpen, setShareOpen] = useState(false);

   class Share extends Component {


   constructor(props) {
    super(props)

    this.state = {
      copySuccess: false
    }
  }
}

  copyCodeToClipboard = () => {
    let url = window.location.href
    document.execCommand("copy")
    this.setState({copySuccess: true})
    alert(url)
  }
    return (
    <div className="Share">
      <button className="Share-toggle" onClick={() => setShareOpen(!shareOpen)}>
        <img src={share} alt="Share Icon" className="Share-icon"/>

        Share
      </button>
      <div className="Share-body">

          <button onClick={() => copyCodeToClipboard}>
            ref={Share}
            value="www.covexit.de"
            message="Share Covexit and help others during this crisis."
         ></button>

        <div>
          <button onClick={() => this.copyCodeToClipboard()}>
            Copy Link
          </button>
          {
            this.state.copySuccess ?
            <div>
              Link Shared!
            </div> : null
          }
        </div>
      </div>
    </div>

  );
}

export default Share;
