import React, { useState} from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share(){

    const message=['Share covexit and help others during this crisis'];
    const [show, setShow] = useState(false);
    const copyLInk = () => {

    url = window.location.href;
    value = "www.covexit.de"
    this.value()
    document.execCommand("copy")
    }
    return (
<>
   <button className="Share-toggle" onClick={() => setShow(!show)}>
      <img src={share} alt="Share Icon" className="Share-icon" />

      Share
      </button>


    <div className="Share-body">
      <h2 className="heading">{message}</h2>
      {value}

       <Share.url />
        <button className="Share" onClick={() => copyLInk()}>
          Copy Link
          </button>

         <button className="Skip" onClick={() => setShow()}>
          Skip
          </button>
      </div>
</>
  )
  }

export default Share;
