import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";

function Share () {
    const [showOpen, setShowOpen] = useState(false);

      copyLink = () => {
      const url = this.window.location.href;
      url.select();
      document.execCommand("copy");
      this.setShow({ copySuccess: true });
    }
    {
      return (
        <div className={`Share Share--${showOpen ? 'opened' : 'closed'}`}>
          <button className="Share-toggle" onClick={() => setShowOpen(!showOpen)}>
            <img src={share} alt="Share Icon" className="Share-icon" />

            Share

      </button>
          <Share className="Share-body">

            <input type="url" value="www.covexit.de"></input>
            <button label="Copy Link" onClick={() => copyLink()}></button>


            <button label="Skip" onClick={() => setShowOpen(false)}>

              Skip
            </button>
          </Share>
        </div>
  )
}
}
export default Share;
