import React, { Component, useState } from "react"
import './Share.scss';
import share from "../../assets/share.svg";

const [show, setShow] = useState(false);

function Share(){

  class Share extends Component({

    copyCodeToClipboard(){
      let url = window.location.href

      document.execCommand("copy")
      alert(url)}
    })
{

   render(){
   <>
   <button className="Share-toggle" onClick={() => setShow(!show)}>
      <img src={share} alt="Share Icon" className="Share-icon" />

      Share
      </button>
    <div className="Share-body">
      <div>
        <div
          value="www.covexit.de"
          message="Share covexit and help others during this crisis."
        />
      </div>
      <div>
        <button className="Share" onClick={() => this.copyCodeToClipboard()}>
          Copy Link
          </button>
      </div>
    </div>
    </>
   }

}
  }

export default Share;
