import './Share.scss';
import share from "../../assets/share.svg";
import Button from "../Button/Button";




function Share() {
    const [show, setShow] = useState(false);
    const message ="Share covexit and help others during this crisis";
    const url = window.location.href;
    const value = "www.covexit.de";

    function copyLink(){
    this.url=document.execCommand("copy");
    }
     alert(url);
  return (


    <div className="Share">
      <button className="Share-toggle" onClick={() => setShow(!show)}>
        <span className="Share-icon"><img src={share} alt="Share Icon" className="Share-icon" />

          Share
      </span>
      </button>
      <div show={show}>
           <p class="message">{message}</p>
           <p class="url">{value}</p>
       <Button className="Copy Link" onClick="copyLink()">


       </Button>

       <Button className="Share">

       </Button>

        </div>
      </div>


  );
}


export default Share;
