import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share() {
    const [show, setShow] = useState(false);

  return (


    <div className="Share">
      <button variant="share-toggle" onClick={() => setShow(!show)}>
        <span className="Share-icon"><img src={share} alt="Share Icon" className="Share-icon" />

          Share
      </span>
      </button>
      <div show={show}>
        <div className="Share-body">
          <div className="Social-links" >

            <FacebookShareButton><FacebookIcon size={32} round={true} /></FacebookShareButton>

            <LinkedinShareButton><LinkedinIcon size={32} round={true} /></LinkedinShareButton>

            <TwitterShareButton><TwitterIcon size={32} round={true} /></TwitterShareButton>


            <EmailShareButton><EmailIcon size={32} round={true} />
            </EmailShareButton>

            <WhatsappShareButton><WhatsappIcon size={32} round={true} />
            </WhatsappShareButton>


            <FacebookMessengerShareButton><FacebookMessengerIcon size={32} round={true} />
            </FacebookMessengerShareButton>
          </div>
        </div>
      </div>
    </div>

  );
}


export default Share;
