import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";
import Button from "../Button/Button";


function  copyLink (){
      const url = window.location.href;
      url.select();
      document.execCommand("copy");
      this.setShow({ copySuccess: true });
    }

function Share () {
    const value="wwww.covexit.de";
    const message="Share covexit and help others during this crisis.";
    const [showOpen, setShowOpen] = useState(false);

      return (
        <div className="Share">
          <button className="Share-toggle" onClick={() => setShowOpen(!showOpen)}>
            <img src={share} alt="Share Icon" className="Share-icon" />

            Share

      </button>


            <div className="message">{message}</div>
            <div class="TextInput">
            <span class="TextInput-label-text"></span>
            <input type="text" class="TextInput-field" name="url" value="www.covexit.de" placeholder="{value}  âˆž">
            </input>

</div>

            <div class="Btn-group">
            <Button label="Copy Link" class="copyLink" onClick={() => setShowOpen(false)}>

            Copy Link
            </Button>


            <Button label="Skip" onClick={() => setShowOpen(false)}>

              Skip
            </Button>
            </div>
            </div>

)
      }

export default Share;
