import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";
import Button from "../Button/Button"




function Share() {
    const [show, setShow] = useState(false);
    const message ="Share Covexit and help others";
    const url = window.location.href;
    const value = "www.covexit.de";

  return (


    <div className="Share">
      <button className="Share-toggle" onClick={() => setShow(!show)}>
        <span className="Share-icon"><img src={share} alt="Share Icon" className="Share-icon" />

          Share
      </span>
      </button>
      <div show={show}>
           <p class="message">{message}</p>
           <p class="url">{url}</p>
       <Button className="Share-toggle">

        Copy Link
       </Button>


        </div>
      </div>


  );
}


export default Share;
