import React from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


export default class Share extends React.Component {
       constructor(props) {
       super(props)

        this.state = {
        copySuccess: false
    }
  }

   copyCodeToClipboard = () => {
    document.execCommand("copy")
    this.setState({copySuccess: true})
  }

  handleCopy (e) {
    e.preventDefault();
    e.clipboardData.setData("www.covexit.de");
  }

  render() {
    return (
    <div id="Share-body">
    <button className="Share-toggle"
        >  <img src={share} alt="Share Icon" className="Share-icon" />
          {" "}
          Share{" "}
        </button>


        <input
          type="text"
          defaultValue="www.covexit.de"
          onCopy={this.handleCopy}
        />
   {
            this.state.copySuccess ?
            <div style={{"color": "green"}}>
              Success!
            </div> : null
          }
   <button class="Btn" onClick={() => this.copyCodeToClipboard()}>
            Copy Link
          </button>

        <button class="Btn Btn--secondary">Skip</button>
    </div>
     );
        }
      }
