import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share() {

    const message ="Share covexit and help others during this crisis";
    const url = window.location.href;
    const value = "www.covexit.de";

    function copyLink(){
    this.url=document.execCommand("copy");
    }

  return (

    <div className="Share">
      <button className="Share-toggle">
      <img src={share} alt="Share Icon" className="Share-icon" />

          Share

      </button>



          <div className="message">{message}</div>
          <div className={url} >{value}</div>




      <div className="Btn-group">
       <button className="Btn Btn-Primary">

        Copy Link
       </button>

       <button class="Btn Btn--Secondary">

        Skip
       </button>
</div>
</div>

  );
}


export default Share;
