import React, { Component, useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


export default class Share extends Component() {

    Share(){
    const [showOpen, setShowOpen] = useState(false);
    const message ="Share covexit and help others during this crisis";

    const value = "www.covexit.de";

    function copyLink(){

        const url = window.location.href;
    }

  return (
   <div className={`Share Share--${showOpen ? 'opened' : 'closed'}`}>
      <button className="Share-toggle" onClick={() => setShowOpen(!showOpen)}>
      <img src={share} alt="Share Icon" className="Share-icon" />

          Share

      </button>
      <div className="Share-body">
        <div className="modal">
          {copyLink()(url =>
            <div className="Copy">
              <button label="Copy Link" onClick={() => setShowOpen(false)}></button>
           </div>)}
 </div>
 </div>
  );
}
    <div className="Share">



       <input type="url" placeholder="{value}"></input>
       <div id="result">

       </div>

       <button className="Btn Btn-Primary" >

        Copy Link
       </button>

       <button className="Btn Btn--Secondary">

        Skip
       </button>
</div>
</div>

  );
}}
