import modal from "../../components/Modal";
import './Share.scss';
import share from "../../assets/share.svg";



function  copyLink(){

      const url = window.location.href;
      url.select();
      document.execCommand("copy");
      this.setShow({ copySuccess: true });
    }

function Share(){
      const value="wwww.covexit.de";
      const message="Share covexit and help others during this crisis.";

class Modal extends React.Component{
  state = {
    show: false
  };
  showModal = e => {
    this.setState({
      show: !this.state.show
    });
  };
  render() {
    return (
      <div className="Share">
          <button className="Share-toggle" onClick={e => {
            this.showModal(e);
          }}
        >  <img src={share} alt="Share Icon" className="Share-icon" />
          {" "}
          Share{" "}
        </button>

 <Modal onClose={this.showModal} show={this.state.show}>

            <div className="message">{message}</div>
            <div class="TextInput">
            <span class="TextInput-label-text"></span>
            <input type="text" class="TextInput-field" name="url" placeholder="        www.covexit.de            âˆž  " disabled>
            </input>

           <div class="Btn-Group">
            <button class="Btn Btn" label="Copy Link">

            Copy Link
            </button>


            <button class="Btn Btn--secondary" label="Skip">

              Skip
            </button>
            </div>
            </div>
        </Modal>
      </div>

    );
  }
}
}
 export default Share;
