  };
  showModal = e => {
    this.setState({
      show: !this.state.show
    });
  };
    constructor(props) {
       super(props)

        this.state = {
        copySuccess: false
    }
  }

   copyCodeToClipboard = () => {
    const url=window.location.href
    this.value.select();
    document.execCommand("copy");
    this.setState({copySuccess: true});

    alert(this.url);
  }

  handleCopy (e) {
    e.preventDefault();
    e.clipboardData.setData(url);

  }
