import React, {useState} from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share() {
  const [ show, setShow]= useState(false);


  return (
    <div className="Share">
      <button className="Share-toggle" onClick={() => setShow(!show)}>
        <img src={share} alt="Share Icon" className="Share-icon"/>

        Share
      </button>

      <div className="Share-body" onClick={() => setShow(false)} >

        <h4>Share covexit and help others during this crisis.</h4>

        <button className="copy-link">

        Copy Link
        </button>



        </div>

      </div>

  );
}

export default Share;
