import React, { Component } from 'react';
import Modal from "../Modal/Modal";
import './Share.scss';
import share from "../../assets/share.svg";
import {CopyToClipboard} from 'react-copy-to-clipboard';


class Share extends React.Component{
  state = {
    show: false
  };
  showModal = e => {
    this.setState({
      show: !this.state.show
   });
  };
  render() {
    return (
      <div className="Share">
          <button className="Share-toggle" onClick={e => {
            this.showModal(e);
          }}
        >  <img src={share} alt="Share Icon" className="Share-icon" />
          {" "}
          Share{" "}
        </button>

 <Modal onClose={this.showModal} show={this.state.show}>

            <div className="heading">Share Covexit and help others with this Crisis.</div>
            <div class="TextInput">
            <span class="TextInput-label-text"></span>
            <input type="text" class="TextInput-field" name="url" placeholder="        www.covexit.de            âˆž  " disabled>
            </input>

           <div class="Btn-Group">

       <CopyToClipboard text={'www.covexit.de'}>
           <button class="Btn">Copy Link</button>
       </CopyToClipboard>

            </div>
            </div>
        </Modal>
      </div>

    );
  }
}
 export default Share;
