import React, { Component, useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


const [showOpen, setShowOpen] = useState(false);

export default class Share extends Component() {
  constructor(props) {
    super(props)

    this.state = {
      copySuccess: false
    }
  }

  copyLink = () => {
    const url = this.window.location.href;
    url.select()
    document.execCommand("copy")
    this.setShow({copySuccess: true})
  }

  render() {
    return (
   <div className={`Share Share--${showOpen ? 'opened' : 'closed'}`}>
      <button className="Share-toggle" onClick={() => setShowOpen(!showOpen)}>
      <img src={share} alt="Share Icon" className="Share-icon" />

          Share

      </button>
      <div className="Share-body">
        <div className="modal">
            <div className="Copy">
              <button label="Copy Link" onClick={() => setShowOpen(false)}></button>
           </div>
 </div>
 </div>
  );
}
    <div className="Share">



