import React, { Component,useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";



function  copyLink (){
      const url = window.location.href;
      url.select();
      document.execCommand("copy");
      this.setShow({ copySuccess: true });
    }

function Share () {
    const value="wwww.covexit.de";
    const message="Share covexit and help others during this crisis.";
    const [showOpen, setShowOpen] = useState(false);

      return (
      <div className="Share">
          <button className="Share-toggle" onClick={() => setShowOpen(!showOpen)}>
            <img src={share} alt="Share Icon" className="Share-icon" />

            Share

           </button>

        <div className="Share-body">
            <div className="message">{message}</div>
            <div class="TextInput">
            <span class="TextInput-label-text"></span>
            <input type="text" class="TextInput-field" name="url" placeholder="        www.covexit.de            âˆž  " disabled>
            </input>

           <div class="Btn-Group">
            <button class="Btn Btn-primary" label="Copy Link" class="copyLink" onClick={() => setShowOpen(false)}>

            Copy Link
            </button>


            <button class="Btn Btn-Secondary" label="Skip" onClick={() => setShowOpen(false)}>

              Skip
            </button>
            </div>
        </div>
    </div>
</div>
)
      }



export default Share;
