import React, { useState } from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share() {
    const [showOpen, setShowOpen] = useState(false);
    const message ="Share covexit and help others during this crisis";
    const url = window.location.href;
    const value = "www.covexit.de";

    function copyLink(){
    this.url=document.execCommand("copy");
    }

  return (

    <div className="Share">
      <button className="Share-toggle">
      <img src={share} alt="Share Icon" className="Share-icon" />

          Share

      </button>


       <input type="url" placeholder="{value}"></input>


       <button className="Btn Btn-Primary">

        Copy Link
       </button>

       <button className="Btn Btn--Secondary">

        Skip
       </button>
</div>


  );
}


export default Share;
