import React, { useState} from 'react';
import './Share.scss';
import share from "../../assets/share.svg";


function Share() {
    const [show, setShow] = useState(false);

    const message=['Share covexit and help others during this crisis'];

    const url = window.location.href;

    function copyLink(){
    url= url;
    this.select();
    document.execCommand("copy")
    }

  return (

    <div className="Share">
      <button className="share-toggle" onClick={() => setShow(!show)}>
        <span className="Share-icon"><img src={share} alt="Share Icon" className="Share-icon" />

          Share
      </span>
      </button>
      <div className="Share-body" {...show}>
            {message}
             {url}
          <button title="Copy Link" onClick={() => copyLink()}>


      </button>
     </div>
</div>
  )
}

export default Share;
